import React from 'react'
import marked from 'marked'

export const Previewer = (props) => {
  const rawMarkDown = () => {
    let mrkdwn = marked(props.input, { sanitize: true });
    return { __html: mrkdwn }
  }
    return (
      <>
        <h1 className="display-5">Previewer</h1>
        <div id="preview" dangerouslySetInnerHTML={rawMarkDown()}></div>
      </>
    );
}
