import React from 'react';

export const Previewer = ({ input }) => {
  return (
    <>
      <h1 className="display-5">Previewer</h1>
      <div
        id="preview"
        dangerouslySetInnerHTML={{ __html: marked(input) }}
      ></div>
    </>
  );
};
