import React from 'react';
import marked from 'marked'

export const Previewer = ({ input }) => {
  const markedInput = { __html: marked(input) };
  return (
    <>
      <h1 className="display-5">Previewer</h1>
      <div
        id="preview"
        dangerouslySetInnerHTML={markedInput}
      ></div>
    </>
  );
};
