import React from 'react'

export const Editor = ({placeholder, handleInput, input}) => {
    return (
      <>
        <h1>Editor</h1>
        <textarea
          name="editor"
          id="editor"
          cols="50"
          rows="30"
          onInput={handleInput}
          value={input}
          defaultValue={placeholder}
        ></textarea>
      </>
    );
}
