import React, { useState } from 'react';
import './App.css';
import { Editor } from './components/Editor';
import { Previewer } from './components/Previewer';
import { Container, Row, Col } from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const placeholder =
    '# H1\n ## H2\n ### H3\n **bold text**\n\n  *italicized text*\n > blockquote\n 1. First item 2. Second item 3. Third item \n - First item - Second item - Third item \n`code` \n 	--- \n[title](https://www.example.com) \n![alt text](image.jpg)';

  const [input, setInput] = useState(placeholder);

  const handleInput = e => {
    setInput(e.target.value);
  };

  return (
    <Container className="container-md">
      <Row>
        <Col>
          <Editor handleInput={handleInput} placeholder={placeholder} />
        </Col>
      </Row>
      <Row>
        <Col>
          <div class="d-block p-4">
          </div>
        </Col>
      </Row>
      <Row>
        <Col className="jumbotron">
          <Previewer input={input} />
        </Col>
      </Row>
    </Container>
  );
}

export default App;
